import React from "react";
import UpdateCounter from "./components/UpdateCounter";
import DisplayTime from "./components/CurrentTime";
import cars from "./components/Destructure/destructuring";
import EventHandler from "./components/eventHandling";

//useState() is a Hook used to change or update the state(variable)

//3--Destructuring ES-6 Concept
console.log(cars); //Array of objects

//Destructuring in arrays--> []
const [honda, tesla] = cars; //Direct Rename simple in array drestructuring
console.log(honda); //an object with nested array and nested object

//Destructuring in objects --> {}
const {
  coloursByPopularity: [hondaTopColor],
  speedStats: { topSpeed: hondaTopSpeed },
} = honda;
console.log(hondaTopColor);

const {
  coloursByPopularity: [teslaTopColor],
  speedStats: { topSpeed: teslaTopSpeed },
} = tesla;
console.log(teslaTopSpeed);

function App() {
  return (
    <div>
      <UpdateCounter />
      <DisplayTime />
      <div>
        <h1>Destructuring in ES-6</h1>
        <table className="styling-table">
          <tr>
            <th>Brand</th>
            <th>Top Speed</th>
            <th>Top Color</th>
          </tr>
          <tr>
            <td>{tesla.model}</td>
            <td>{teslaTopSpeed}</td>
            <td>{teslaTopColor}</td>
          </tr>
          <tr>
            <td>{honda.model}</td>
            <td>{hondaTopSpeed}</td>
            <td>{hondaTopColor}</td>
          </tr>
        </table>
      </div>
      <EventHandler />
    </div>
  );
}

export default App;

